import React, { useEffect } from "react";
import axios from "axios";
import "./Chatwoot.css";

const BASE_URL = "http://127.0.0.1:3000"; // Your Chatwoot instance URL
const WEBHOOK_URL = "http://localhost:3200/webhook"; // Make sure this matches your backend port

const ChatwootWidget = () => {
  useEffect(() => {
    (function (d, t) {
      const scriptElem = d.createElement(t);
      const firstScript = d.getElementsByTagName(t)[0];

      scriptElem.src = BASE_URL + "/packs/js/sdk.js";
      scriptElem.defer = true;
      scriptElem.async = true;
      firstScript.parentNode.insertBefore(scriptElem, firstScript);

      scriptElem.onload = function () {
        window.chatwootSDK.run({
          websiteToken: "BH9YQA553U754XQuLQmZYD5p", // Replace with your Chatwoot website token
          baseUrl: BASE_URL,
          onMessage: async (message) => {
            console.log("Received message object:", message);
            console.log("Message content:", message.content);
            console.log("Conversation ID:", message.conversation_id || message.conversationId);

            if (
              message.content &&
              message.content.trim().toLowerCase().startsWith("/ask")
            ) {
              const queryText = message.content.trim().substring(4).trim();
              console.log("Extracted Query Text:", queryText);

              try {
                console.log("Sending POST request to webhook:", WEBHOOK_URL);
                await axios.post(WEBHOOK_URL, {
                  payload: { content: message.content },
                  conversation: { id: message.conversation_id || message.conversationId },
                });
                console.log("Webhook triggered successfully.");
              } catch (error) {
                console.error("Error sending message to webhook:", error);
              }
            }
          },
        });
      };
    })(document, "script");
  }, []);

  return null;
};

export default ChatwootWidget;
