import React, { useEffect, useState } from "react";
import keycloak from "./keycloakConfig";
import SearchComponent from "./SearchComponent"; // ✅ Import SearchComponent
import "./App.css"; // ✅ Import CSS file
import axios from "axios";

const CHATWOOT_API_URL = "http://localhost:3000/api/v1"; // ✅ Replace if running remotely
const CHATWOOT_API_KEY = "ExxBwFzRFtia8TiA8gv8pYrC"; // ✅ Replace with actual API key

const App = () => {
  const [authenticated, setAuthenticated] = useState(false);
  const [query, setQuery] = useState("");

  useEffect(() => {
    console.log("Initializing Keycloak...");
    keycloak
      .init({
        onLoad: "login-required",
        checkLoginIframe: false,
      })
      .then((auth) => {
        setAuthenticated(auth);
        console.log("Keycloak initialized:", auth);
      })
      .catch((err) => {
        console.error("Keycloak initialization failed:", err);
      });
  }, []);

  // ✅ Send an `/ask` Query to Chatwoot
  const sendQuery = async () => {
    try {
      await axios.post(
        `${CHATWOOT_API_URL}/accounts/1/messages`,
        {
          content: `/ask ${query}`,
          message_type: "incoming",
        },
        {
          headers: { Authorization: `Bearer ${CHATWOOT_API_KEY}` },
        }
      );
      console.log("Query sent!");
    } catch (error) {
      console.error("Error sending query:", error);
    }
  };

  return (
    <div className="app-container">
      {console.log(authenticated)}
      {authenticated ? (
        <>
          <h2 className="welcome">
            Welcome {keycloak.tokenParsed?.preferred_username}!
          </h2>

          <div className="search-component">
            <SearchComponent /> {/* ✅ Wrapped in a container for styling */}
          </div>
          <div>
            <ChatwootWidget />
          {/* ✅ Input for `/ask` Queries */}
          <h2>Ask a Question</h2>
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          <button onClick={sendQuery}>Ask Outline</button>
          </div>
        </>
      ) : (
        <h2 className="redirecting">Redirecting to login...</h2>
      )}
    </div>
  );
};

export default App;
