import React, { useEffect, useState } from "react";
import keycloak from "./keycloakConfig";
import SearchComponent from "./SearchComponent"; // âœ… Import SearchComponent
import "./App.css"; // âœ… Import CSS file
import axios from "axios";

const CHATWOOT_API_URL = "http://localhost:3000/api/v1";  // ðŸ”¹ Replace if running remotely
const CHATWOOT_API_KEY = "ExxBwFzRFtia8TiA8gv8pYrC";      // ðŸ”¹ Replace with actual API key

const App = () => {
    const [authenticated, setAuthenticated] = useState(false);
    const [query, setQuery] = useState("");
    const [conversations, setConversations] = useState([]);

    useEffect(() => {
        console.log("Initializing Keycloak...");

        keycloak.init({ 
            onLoad: "login-required", 
            checkLoginIframe: false 
        }).then((auth) => {
            setAuthenticated(auth);
            console.log("Keycloak initialized:", auth);

            // âœ… Fetch Chatwoot Conversations on Login
            if (auth) {
                fetchConversations();
            }
        }).catch(err => {
            console.error("Keycloak initialization failed:", err);
        });

    }, []);

    // ðŸ”¹ Function to Fetch Conversations from Chatwoot
    const fetchConversations = async () => {
        try {
            const response = await axios.get(`${CHATWOOT_API_URL}/accounts/1/conversations`, {
                headers: { Authorization: `Bearer ${CHATWOOT_API_KEY}` }
            });
            setConversations(response.data);
        } catch (error) {
            console.error("Error fetching conversations:", error);
        }
    };

    // ðŸ”¹ Function to Send an `/ask` Query to Chatwoot
    const sendQuery = async () => {
        try {
            await axios.post(`${CHATWOOT_API_URL}/accounts/1/messages`, {
                content: `/ask ${query}`,
                message_type: "incoming",
            }, {
                headers: { Authorization: `Bearer ${CHATWOOT_API_KEY}` }
            });
            console.log("Query sent!");
        } catch (error) {
            console.error("Error sending query:", error);
        }
    };

    return (
        <div className="app-container">
            {authenticated ? (
                <>
                    <h2 className="welcome">Welcome {keycloak.tokenParsed?.preferred_username}!</h2>
                    <div className="search-component">
                        <SearchComponent /> {/* âœ… Wrapped in a container for styling */}
                    </div>

                    {/* ðŸ”¹ Display Chatwoot Conversations */}
                    <h2>Chatwoot Conversations</h2>
                    <ul>
                        {conversations.map(conv => (
                            <li key={conv.id}>Conversation {conv.id}: {conv.messages[0]?.content}</li>
                        ))}
                    </ul>

                    {/* ðŸ”¹ Input for `/ask` Queries */}
                    <h2>Ask a Question</h2>
                    <input type="text" value={query} onChange={(e) => setQuery(e.target.value)} />
                    <button onClick={sendQuery}>Ask Outline</button>

                </>
            ) : (
                <h2 className="redirecting">Redirecting to login...</h2>
            )}
        </div>
    );
};

export default App;
