import React, { useEffect } from "react";

const ChatwootWidget = () => {
  useEffect(() => {
    // Immediately-Invoked Function Expression (IIFE) to load the Chatwoot SDK
    (function(d, t) {
      // Set the base URL to your local Chatwoot instance (adjust if needed)
      var BASE_URL = "http://0.0.0.0:3000";

      // Create a script element that loads the Chatwoot SDK
      var scriptElem = d.createElement(t),
          firstScript = d.getElementsByTagName(t)[0];

      scriptElem.src = BASE_URL + "/packs/js/sdk.js";
      scriptElem.defer = true;
      scriptElem.async = true;

      // Insert the script into the DOM
      firstScript.parentNode.insertBefore(scriptElem, firstScript);

      // When the script loads, run the Chatwoot widget
      scriptElem.onload = function() {
        if (window.chatwootSDK) {
          window.chatwootSDK.run({
            websiteToken: "sBJyEHCv2C52bcE5dHE2qFZi", // Replace with your actual website token
            baseUrl: BASE_URL
          });
        }
      };
    })(document, "script");
  }, []);

  // This component does not render visible UI—its job is just to dynamically load the script.
  return null;
};

export default ChatwootWidget;
