import React, { useEffect } from "react";
import './Chatwoot.css';

const ChatwootWidget = () => {
  useEffect(() => {
    (function(d, t) {
      const BASE_URL = "http://0.0.0.0:3000"; // Your Chatwoot URL
      const scriptElem = d.createElement(t);
      const firstScript = d.getElementsByTagName(t)[0];

      scriptElem.src = BASE_URL + "/packs/js/sdk.js";
      scriptElem.defer = true;
      scriptElem.async = true;
      firstScript.parentNode.insertBefore(scriptElem, firstScript);

      scriptElem.onload = function() {
        if (window.chatwootSDK) {
          // Initialize the widget using your website token
          window.chatwootSDK.run({
            websiteToken: "sBJyEHCv2C52bcE5dHE2qFZi", // Replace with your token
            baseUrl: BASE_URL
          });

          // First, try to use the SDK methods to open the widget.
          setTimeout(() => {
            if (window.chatwootSDK.show) {
              window.chatwootSDK.show();
            } else if (window.chatwootSDK.toggle) {
              window.chatwootSDK.toggle();
            }
          }, 500);

          // Next, try simulating a click on the chat launcher element.
          // This will poll for the launcher's existence and click it.
          let checkCount = 0;
          const intervalId = setInterval(() => {
            const launcher = document.querySelector(".chatwoot-launcher");
            if (launcher) {
              launcher.click();
              clearInterval(intervalId);
            }
            checkCount++;
            if (checkCount > 10) { // Try for about 5 seconds (10 x 500ms)
              clearInterval(intervalId);
            }
          }, 500);
        }
      };
    })(document, "script");

    // Inject custom CSS (optional), e.g., for a pulsing effect on the launcher
    const styleElement = document.createElement("style");
    styleElement.innerHTML = `
      .chatwoot-launcher {
         animation: pulse 2s infinite;
      }
      @keyframes pulse {
         0% { transform: scale(1); }
         50% { transform: scale(1.1); }
         100% { transform: scale(1); }
      }
    `;
    document.head.appendChild(styleElement);
  }, []);

  return null; // This component doesn't render visible output.
};

export default ChatwootWidget;
